import React, { useState } from "react";
import { Products } from "./Products"
import { Categories } from "./Categories"
import logo from './logo.png';
import './App.css';

const render_products = (ProductsCategory) => {
  return (
    <div className='category-section fixed'>
      <h2 className="text-3xl font-extrabold tracking-tight text-gray-600 category-title">Products ({ProductsCategory.length})</h2>
      <div className="m-6 p-3 mt-10 ml-0 grid grid-cols-1 gap-y-10 gap-x-6 sm:grid-cols-2 lg:grid-cols-6 xl:gap-x-10" style={{ maxHeight: '800px', overflowY: 'scroll' }}>
        {/* Loop Products */}
        {ProductsCategory.map((product, index) => (
          <div key={index} className="group relative shadow-lg">
            <div className="min-h-80 bg-gray-200 aspect-w-1 aspect-h-1 rounded-md overflow-hidden group-hover:opacity-75 lg:h-60 lg:aspect-none">
              <img
                alt="Product Image"
                src={product.image}
                className="w-full h-full object-center object-cover lg:w-full lg:h-full"
              />
            </div>
            <div className="flex justify-between p-3">
              <div>
                <h3 className="text-sm text-gray-700">
                  <a href={product.href}>
                    <span aria-hidden="true" className="absolute inset-0" />
                    <span style={{ fontSize: '16px', fontWeight: '600' }}>{product.title}</span>
                  </a>
                  <p>Tag - {product.category}</p>
                </h3>
                <p className="mt-1 text-sm text-gray-500">Rating: {product.rating.rate}</p>
              </div>
              <p className="text-sm font-medium text-green-600">${product.price}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

const App = () => {
  console.log("Step 1 : load Products in a useState.");
  const [ProductsCategory, setProductsCategory] = useState(Products);

  const handleClick = (tag) => {
    console.log("Step 4: in handleClick", tag);
    let filtered = Products.filter(product => product.category === tag);
    setProductsCategory(filtered);
    console.log("Step 5:", Products.length, ProductsCategory.length);
  };

  return (
    <div className="flex">
      <div className="navigation-bar bg-slate-800 p-3">
        <img className="logo" src={logo} alt="Company Logo" />
        <div className="categories">
          {Categories.map((tag) => (
            <button
              key={tag}
              onClick={() => handleClick(tag)}
              className="category-button"
            >
              {tag}
            </button>
          ))}
        </div>
      </div>
      <div className="product-list">
        {render_products(ProductsCategory)}
      </div>
    </div>
  );
};
export { App };
