import React, { useState } from "react";
import { Products } from "./Products"
import { Categories } from "./Categories"
import logo from './logo.png';
import './App.css';

const render_products = (ProductsCategory) => {
  return (
    <div className="products-container">
    {ProductsCategory.map((product, index) => (
      <div key={index} className="product">
        <img src={product.image} alt={product.title} className="product-image" />
        <div className="product-info">
          <h3 className="product-title">{product.title}</h3>
          <p className="product-category">Category: {product.category}</p>
          <p className="product-rating">Rating: {product.rating.rate}</p>
          <p className="product-price">${product.price}</p>
        </div>
      </div>
    ))}
  </div>

  );
}

const App = () => {
  console.log("Step 1 : load Products in a useState.");
  const [ProductsCategory, setProductsCategory] = useState(Products);

  const handleClick = (category) => {
    // Filter products by category
    const filteredProducts = Products.filter(product => product.category === category);
    setProductsCategory(filteredProducts);
  };

  return (
    <div className="flex fixed flex-row">
      <div className="h-screen bg-slate-800 p-3 xl:basis-1/5" style={{ minWidth: '250px' }}>
        <img className="w-full" src={logo} alt="Logo" />
        <div className="px-6 py-4">
          <h1 className="text-3xl mb-2 font-bold text-white">Product Catalog App</h1>
          <p className="text-white">
            by - <b style={{ color: 'orange' }}>Design Shubham, Development Abraham</b>
          </p>
          <div className="py-10">
            {Categories && <p className='text-white'>Tags:</p>}
            {Categories.map(tag => (
              <button
                key={tag}
                onClick={() => setProductsCategory(Products.filter(product => product.category === tag))}
                className="inline-block bg-amber-600 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mt-2"
              >
                {tag}
              </button>
            ))}
          </div>
        </div>
      </div>
      <div className="ml-5 p-10 xl:basis-4/5">
        {render_products(ProductsCategory)}
      </div>
    </div>
  );
};
export { App };
