import React, { useState } from "react";
import { Products } from "./Products";
import { Categories } from "./Categories";
import logo from './logo.png';
import './App.css';

const render_products = (ProductsCategory) => {
  return (
    <div className="products-container">
      {ProductsCategory.map((product, index) => (
        <div key={index} className="product">
          <img src={product.image} alt={product.title} className="product-image" />
          <div className="product-info">
            <h3 className="product-title">{product.title}</h3>
            <p className="product-category">Category: {product.category}</p>
            <p className="product-rating">Rating: {product.rating.rate}</p>
            <p className="product-price">${product.price}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

const App = () => {
  const [ProductsCategory, setProductsCategory] = useState(Products);

  const handleClick = (category) => {
    const filteredProducts = Products.filter(product => product.category === category);
    setProductsCategory(filteredProducts);
  };

  return (
    <div className="flex">
      <div className="navigation-bar bg-slate-800 p-3">
        <img className="logo" src={logo} alt="Company Logo" />
        <div className="categories">
          {Categories.map((category) => (
            <button
              key={category}
              onClick={() => handleClick(category)}
              className="category-button bg-amber-600 rounded-full px-3 py-1 text-sm font-semibold text-gray-700 mr-2 mt-2"
            >
              {category}
            </button>
          ))}
        </div>
      </div>
      <div className="product-list">
        {render_products(ProductsCategory)}
      </div>
    </div>
  );
};

export default App;
